import React, { useState } from 'react';
import Calendar from 'react-calendar'; // Import the calendar library
import 'react-calendar/dist/Calendar.css'; // Import default styling for the calendar
import EventForm from './EventForm'; // Your event form component to create events
import { useEffect } from 'react';

const CalendarUI = ({ events, onEventSave }) => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [showEventForm, setShowEventForm] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);

  // Handle calendar date change
  const handleDateChange = (date) => {
    setSelectedDate(date);
    setSelectedEvent(null); // Clear the selected event when the date changes
    setShowEventForm(true); // Show the event form when a date is selected
  };

  // Show events for the selected date
  const getEventsForSelectedDate = (date) => {
    return events.filter(event => new Date(event.date).toDateString() === new Date(date).toDateString());
  };

  // Handle saving an event
  const handleSaveEvent = (eventData) => {
    onEventSave(eventData);
    setShowEventForm(false); // Close the form after saving
  };
  const requestNotificationPermission = () => {
    if ('Notification' in window) {
      Notification.requestPermission().then(permission => {
        if (permission === 'granted') {
          console.log('Notification permission granted');
        }
      });
    }
  };
  
  useEffect(() => {
    requestNotificationPermission();
  }, []);
  const sendEventNotification = (event) => {
    if (Notification.permission === 'granted') {
      const notification = new Notification(event.title, {
        body: `Your event "${event.title}" is starting at ${event.time}`,
        icon: '/path-to-icon.png', // Add an icon if you like
        actions: [{ action: 'snooze', title: 'Snooze for 5 minutes' }]
      });
  
      notification.onclick = () => {
        console.log('Event notification clicked');
        notification.close();
      };
  
      // Event listener for snooze action
      notification.addEventListener('click', (e) => {
        if (e.action === 'snooze') {
          console.log('Snoozing event for 5 minutes');
          setTimeout(() => {
            sendEventNotification(event); // Re-send the notification after 5 minutes
          }, 5 * 60 * 1000); // 5 minutes in milliseconds
          notification.close(); // Close the original notification
        }
      });
  
      // Auto-dismiss the notification after 5 seconds if not interacted with
      setTimeout(() => {
        notification.close();
      }, 5000); // 5 seconds timeout
    }
  };
  const checkEventNotifications = () => {
    const now = new Date();
    events.forEach(event => {
      const eventDate = new Date(event.date + ' ' + event.time); // Combine date and time
      if (eventDate <= now && !event.notified) {
        sendEventNotification(event);
        event.notified = true; // Prevent sending duplicate notifications
      }
    });
  };
  
  // Check every minute if any event time is due
  useEffect(() => {
    const interval = setInterval(() => {
      checkEventNotifications();
    }, 60000); // 60,000ms = 1 minute
  
    return () => clearInterval(interval); // Cleanup on component unmount
  }, [events]);
    
  return (
    <div className="calendar-ui">
      <h2>Event Calendar</h2>
      <Calendar
        onChange={handleDateChange} // Update selected date
        value={selectedDate} // Set the selected date on the calendar
      />
      <div className="event-list">
        {selectedDate && (
          <>
            <h3>Events for {selectedDate.toDateString()}</h3>
            <ul>
              {getEventsForSelectedDate(selectedDate).map(event => (
                <li key={event.id}>
                  <strong>{event.title}</strong>
                  <p>{event.description}</p>
                  <p>{event.time}</p>
                  {event.media && (
                    <div>
                      {event.media.type === 'image' && <img src={event.media.url} alt={event.title} />}
                      {event.media.type === 'video' && <video controls src={event.media.url} />}
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </>
        )}
      </div>

      {showEventForm && (
        <EventForm
          event={selectedEvent}
          onSave={handleSaveEvent} // Handle form submission
        />
      )}
    </div>
  );
};

export default CalendarUI;
