import React, { useState } from 'react';

const EventForm = ({ event, onSave }) => {
  const [formData, setFormData] = useState({
    title: '',
    date: '',
    time: '',
    description: '',
    media: { type: '', url: '' }, // New field for media
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle media file input
  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const url = URL.createObjectURL(file);
      setFormData({
        ...formData,
        media: { type: file.type.startsWith('image') ? 'image' : 'video', url },
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.title || !formData.date || !formData.time) {
      alert('Please fill in all required fields.');
      return;
    }
    onSave({ ...formData, id: new Date().toISOString() }); // Generate unique ID
    setFormData({ title: '', date: '', time: '', description: '', media: { type: '', url: '' } });
  };

  return (
    <div className="event-form">
      <h2>{event ? 'Edit Event' : 'Create Event'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Date:</label>
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Time:</label>
          <input
            type="time"
            name="time"
            value={formData.time}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Attach Media (Image or Video):</label>
          <input
            type="file"
            onChange={handleMediaChange}
          />
        </div>
        <button type="submit">{event ? 'Save Changes' : 'Create Event'}</button>
      </form>
    </div>
  );
};

export default EventForm;
