import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import EventForm from './EventForm';

const CalendarUI = ({ events, onEventSave }) => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [showEventForm, setShowEventForm] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);

  // Handle date change
  const handleDateChange = (date) => {
    setSelectedDate(date);
    setSelectedEvent(null);  // Clear any selected event
    setShowEventForm(true);  // Open the form for a new event
  };

  // Get events for the selected date
  const getEventsForSelectedDate = (date) => {
    return events.filter(event => new Date(event.date).toDateString() === new Date(date).toDateString());
  };

  // Save event and close form
  const handleSaveEvent = (eventData) => {
    onEventSave(eventData);  // Save event via parent function
    setShowEventForm(false);  // Close form
  };

  return (
    <div className="calendar-ui">
      <h2>Event Calendar</h2>
      <Calendar
        onChange={handleDateChange}  // Trigger date selection
        value={selectedDate}  // Show the selected date on calendar
      />
      
      <div className="event-list">
        {selectedDate && (
          <>
            <h3>Events for {selectedDate.toDateString()}</h3>
            <ul>
              {getEventsForSelectedDate(selectedDate).map(event => (
                <li key={event.id}>
                  <strong>{event.title}</strong>
                  <p>{event.description}</p>
                  <p>{event.time}</p>
                </li>
              ))}
            </ul>
          </>
        )}
      </div>

      {showEventForm && (
        <EventForm
          event={selectedEvent}
          onSave={handleSaveEvent}  // Save event when form is submitted
        />
      )}
    </div>
  );
};

export default CalendarUI;
